import React, { Component } from "react";
import "../App.css";
import Gallery from "react-grid-gallery";
import HeartRates from "../images/HeartRates.jpg";

const IMAGES = [
  {
    src: "https://i.ibb.co/vDTckxF/Heart-Rates.jpg",
    thumbnail: "https://i.ibb.co/vDTckxF/Heart-Rates.jpg",
    thumbnailWidth: 320,
    thumbnailHeight: 304,
    caption: "Heart Rates"
  },

  {
    src: "https://i.ibb.co/tHw0XfM/Steps-Graph.jpg",
    thumbnail: "https://i.ibb.co/tHw0XfM/Steps-Graph.jpg",
    thumbnailWidth: 320,
    thumbnailHeight: 212,
    caption: "Steps"
  }
];

/*home page */
class App extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      isLoading: true,
      caregiver: {},
      error: null
    };
  }
  fetchUsers(id) {
    // Where we're fetching data from
    fetch(`https://5cdc6232069eb30014202d8e.mockapi.io/caregivers/${id}`)
      // We get the API response and receive data in JSON format...
      .then(response => response.json())
      // ...then we update the users state
      .then(data =>
        this.setState({
          caregiver: data,
          isLoading: false
        })
      )
      // Catch any errors we hit and update the app
      .catch(error => this.setState({ error, isLoading: false }));
  }
  componentDidMount() {
    this.fetchUsers(12);
  }

  render() {
    return (
      <div>
        <h1>Today's Activities</h1>
        <Gallery images={IMAGES} />
      </div>
    );
  }
}
export default App;
