import React, { Component } from "react";
import "../../App.css";
import { userService } from "../../service";
import { ButtonToolbar, Button } from "react-bootstrap";

//one component that handles both primary and second caregiver contact info
class CaregiverContact extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      caregiverAddresses: [],
      caregiverPhones: [],
      caregiverEmails: [],
      homeAddress: {},
      newHA: {}
    };
  }
  componentDidMount() {
    //get caregivers' contact info

    userService
      .getCaregiverContactInfo(
        JSON.parse(localStorage.getItem("oneUser")).client_id,
        JSON.parse(localStorage.getItem("oneUser")).token,
        1,
        this.props.id
      )

      .then(data => {
        this.setState({
          caregiverAddresses: data[0][1],
          homeAddress: data[0][1].filter(word => word.type == "home"),
          caregiverPhones: data[1][1],
          caregiverEmails: data[2][1]
        });
      });
  }

  render() {
    console.log("HHHHHHHH:" + this.state.homeAddress);
    return (
      <div>
        {!this.props.isEditCaregivers ? (
          <div>
            <li>
              <strong> Home Address: </strong>{" "}
              {this.state.caregiverAddresses.map(p => (
                <div class="inline">
                  {p.type == "home" ? <div>{p.address}</div> : <div />}
                </div>
              ))}
            </li>
            <li>
              <strong> Mailing Address:</strong>{" "}
              {this.state.caregiverAddresses.map(p => (
                <div class="inline">
                  {p.type == "mailing" ? <div>{p.address}</div> : <div />}
                </div>
              ))}
            </li>
            <li>
              <strong>Cell Phone: </strong>
              {this.state.caregiverPhones.map(p => (
                <div class="inline">
                  {p.type == "cell" ? <div>{p.number}</div> : <div />}
                </div>
              ))}
            </li>
            <li>
              <strong> Home Phone:</strong>{" "}
              {this.state.caregiverPhones.map(p => (
                <div class="inline">
                  {p.type == "home" ? <div>{p.number}</div> : <div />}
                </div>
              ))}
            </li>
            <strong> Email:</strong> {this.state.caregiverEmails}
          </div>
        ) : (
          <div>
            <li>
              <strong> Home Address: </strong>{" "}
              <input
                placeholder="home address"
                value={this.state.newHA || ""}
                onChange={this.handleChangeP}
                name="newHA"
              />
            </li>
            <li>
              <strong> Mailing Address:</strong>{" "}
              <input
                placeholder="mailing address"
                value={this.state.newMA || ""}
                onChange={this.handleChangeP}
                name="newMA"
              />
            </li>
            <li>
              <strong>Cell Phone: </strong>
              <input
                placeholder="cell phone"
                value={this.state.newCP || ""}
                onChange={this.handleChangeP}
                name="newCP"
              />
            </li>
            <li>
              <strong> Home Phone:</strong>{" "}
              <input
                placeholder="home phone"
                value={this.state.newHP || ""}
                onChange={this.handleChangeP}
                name="newHP"
              />
            </li>
            <strong> Email:</strong>
            <input
              type="email"
              value={this.state.newEmail}
              onChange={this.handleChangeP}
              name="newEmail"
            />
          </div>
        )}
      </div>
    );
  }
}

export default CaregiverContact;
