import React, { Component } from "react";
import "../../App.css";
import { userService } from "../../service";
import { ButtonToolbar, Button } from "react-bootstrap";
import AddContact from "./addContact";
import CaregiverContact from "./caregiverContact";
import EditCaregiverContact from "./editCaregiverContact";

/* component for displaying & editing Caregivers */
//Two api calls, one for name and relationship, the other for contact info

class EditCaregivers extends React.Component {
  constructor(props, context) {
    super(props, context);
    const clientToEdit = JSON.parse(localStorage.getItem("caregiver"));
    const caregiverToEdit = JSON.parse(
      localStorage.getItem("caregiverContactInfo")
    );
    this.state = {
      isLoading: true,
      caregiver: clientToEdit,
      isEditCaregivers: false,
      isAddContact: false,
      name: "",
      relationship: "",
      is_primary: true,
      primaryName: [],
      primaryRelationship: [],
      newPN: "",
      newPR: "",
      secondName: [],
      secondRelationship: [],
      newSPN: "",
      newSPR: "",

      //break caregivers' contact info down to address, phone and email

      caregiverAddresses: [],
      caregiverPhones: [],
      caregiverEmails: [],

      caregiverAddresses2: [],
      caregiverPhones2: [],
      caregiverEmails2: [],

      homeAddress: {},
      mailingAddress: {},
      homePhone: {},
      cellPhone: {},

      homeAddress2: {},
      mailingAddress2: {},
      homePhone2: {},
      cellPhone2: {},

      //new input values:
      newMA: {},
      newHP: {},
      newCP: {},
      newEmail: {},
      newHA: {},
      errors: {},

      newMA2: {},
      newHP2: {},
      newCP2: {},
      newEmail2: {},
      newHA2: {}
    };
    this.editCancel = this.editCancel.bind(this);
    this.editCaregivers = this.editCaregivers.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.addContact = this.addContact.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);

    this.handleChangeP = this.handleChangeP.bind(this);
  }

  componentDidMount() {
    /* edit name or relationship */

    //primary contact
    const result = this.state.caregiver.filter(word => word.is_primary == true);
    var t = [];
    var r = [];

    for (let i = 0; i < result.length; i++) {
      t.push(result[i].name);
      r.push(result[i].relationship);
    }

    this.setState({
      primaryName: t,
      primaryRelationship: r,
      newPN: t,
      newPR: r
    });

    //second contact
    const result2 = this.state.caregiver.filter(
      word => word.is_primary == false
    );
    var t2 = [];
    var r2 = [];

    for (let i = 0; i < result2.length; i++) {
      t2.push(result2[i].name);
      r2.push(result2[i].relationship);
    }

    this.setState({
      secondName: t2,
      secondRelationship: r2,
      newSPN: t2,
      newSPR: r2
    });
    this.setState({});

    /* get caregivers' contact info */

    const ha = this.state.caregiverAddresses.filter(
      word => word.type == "home"
    );
    if (ha.length != 0) {
      this.setState({ homeAddress: ha[0].address, newHA: ha[0].address });
    } else {
      this.setState({ homeAddress: ha[0], newHA: ha[0] });
    }

    const ma = this.state.caregiverAddresses.filter(
      word => word.type == "mailing"
    );
    if (ma.length != 0) {
      this.setState({ mailingAddress: ma[0].address, newMA: ma[0].address });
    } else {
      this.setState({ mailingAddress: ma[0], newMA: ma[0] });
    }

    const cp = this.state.caregiverPhones.filter(word => word.type == "cell");
    if (cp.length != 0) {
      this.setState({
