import React, { Component } from "react";
import "../App.css";
import { Col, Row, Nav, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { TabContainer, TabPane, TabContent } from "react-bootstrap";
import { userService } from "../service";
import BasicInfo from "./Edit/basicInfo";
import EditProfile from "./Edit/editProfile";
import EditContact from "./Edit/editContact";
import EditCaregivers from "./Edit/editCaregivers";

class Profile extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      user: {},
      address: {},
      c: {},
      caregiver: [],
      isLoading: true,
      healthProfile: [],
      client: {},
      basicInfo: {},
      addressInfo: [],
      physician: [],
      phoneInfo: [],
      emailInfo: [],
      caregiver_client_id: []
    };
  }

  componentDidMount() {
    this.setState({
      user: JSON.parse(localStorage.getItem("oneUser"))
      //basicInfo: JSON.parse(localStorage.getItem("basicInfo"))
    });

    userService
      .getCaregivers(JSON.parse(localStorage.getItem("oneUser")).client_id)
      .then(data =>
        this.setState({
          c: JSON.parse(data)
        })
      );

    /***
     * real api calls
     * contactInfo JSON format has error when printing
     */

    userService
      .getBasicInfo(
        JSON.parse(localStorage.getItem("oneUser")).client_id,
        JSON.parse(localStorage.getItem("oneUser")).user_id,
        JSON.parse(localStorage.getItem("oneUser")).token
      )
      .then(data => this.setState({ basicInfo: data }));
    userService
      .getHealthProfile(
        JSON.parse(localStorage.getItem("oneUser")).client_id,
        JSON.parse(localStorage.getItem("oneUser")).token
      )
      .then(data => this.setState({ healthProfile: data }));

    userService
      .getContactInfo(
        JSON.parse(localStorage.getItem("oneUser")).client_id,
        1,
        JSON.parse(localStorage.getItem("oneUser")).token
      )
      .then(data =>
        this.setState({
          contactInfo: data
        })
      );

    userService
      .getCaregiver(
        JSON.parse(localStorage.getItem("oneUser")).client_id,
        JSON.parse(localStorage.getItem("oneUser")).token,
        1
      )
      .then(data => this.setState({ caregiver: data }));

    userService
      .getPhysician(
        JSON.parse(localStorage.getItem("oneUser")).client_id,
        JSON.parse(localStorage.getItem("oneUser")).token,
        1
      )
      .then(data =>
        this.setState({
          physician: data
        })
      );

    //user from real api
    console.log("user:" + localStorage.getItem("oneUser"));
    console.log("basic info:" + localStorage.getItem("basicInfo"));
    console.log("physician:" + localStorage.getItem("physician"));
    localStorage.setItem("addressInfo", this.state.addressInfo);
  }

  render() {
    const { physician } = this.state;
    console.log("db test:" + this.state.emailInfo);

    return (
      <TabContainer
        id="left-tabs-example"
        className="tab-container"
        defaultActiveKey="first"
      >
        <Row>
          <Col sm={3}>
            <Nav className="flex-column">
              <Nav.Item>
                <Nav.Link
                  class="nav-item"
                  style={{
                    color: "grey",
                    paddingTop: "50px",
                    paddingBottom: "50px"
                  }}
                  eventKey="first"
                >
                  Basic Info
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  class="nav-item"
                  style={{
                    color: "black",
                    paddingTop: "50px",
                    paddingBottom: "50px"
                  }}
                  eventKey="second"
                >
                  Health Profile
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  class="nav-item"
                  style={{
                    color: "black",
                    paddingTop: "50px",
                    paddingBottom: "50px"
                  }}
                  eventKey="third"
                >
                  Contact Info
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  class="nav-item"
                  style={{
                    color: "black",
                    paddingTop: "50px",
                    paddingBottom: "50px"
                  }}
                  eventKey="fourth"
                >
                  Caregivers
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  class="nav-item"
                  style={{
                    color: "black",
                    paddingTop: "50px",
                    paddingBottom: "50px"
                  }}
                  eventKey="fifth"
                >
                  Physicians
                </Nav.Link>
              </Nav.Item>
            </Nav>
          </Col>
          <Col sm={9}>
            <TabContent class="tab-content">
              <TabPane eventKey="first">
                <BasicInfo />
              </TabPane>
              <TabPane eventKey="second">
                <EditProfile />
              </TabPane>
              <TabPane eventKey="third">
                <EditContact />
              </TabPane>
              <TabPane eventKey="fourth">
                <EditCaregivers />
              </TabPane>
              <TabPane eventKey="fifth">
                <div>
                  {physician.map(p => (
                    <div class="inline">
                      <p>
                        <strong>From {p.Healthcare_provider_name}: </strong>{" "}
                      </p>
                      <ul>
                        <li>
                          <strong> Name:</strong> {p.physician_name}
                        </li>
                        <li>
                          <strong> Address:</strong> {p.address}
                        </li>
                        <li>
                          <strong> Phone:</strong> {p.number}
                        </li>
                      </ul>
                      <p />
                    </div>
                  ))}
                </div>
              </TabPane>
            </TabContent>
          </Col>
        </Row>
      </TabContainer>
    );
  }
}

export default Profile;
