import React, { Component } from "react";
import "../../App.css";
import { userService } from "../../service";
import { ButtonToolbar, Button } from "react-bootstrap";
import AddContact from "./addContact";
import CaregiverContact from "./caregiverContact";
import EditCaregiverContact from "./editCaregiverContact";

//TO DO: After Save!!!!
/*edit components */

class EditCaregivers extends React.Component {
  constructor(props, context) {
    super(props, context);
    const clientToEdit = JSON.parse(localStorage.getItem("caregiver"));
    const caregiverToEdit = JSON.parse(
      localStorage.getItem("caregiverContactInfo")
    );
    this.state = {
      isLoading: true,
      caregiver: clientToEdit,
      isEditCaregivers: false,
      isAddContact: false,
      name: "",
      relationship: "",
      is_primary: true,
      primaryName: [],
      primaryRelationship: [],
      newPN: "",
      newPR: "",
      secondName: [],
      secondRelationship: [],
      newSPN: "",
      newSPR: "",

      //two caregivers
      id1: "",
      id2: "",
      //break caregivers' contact info down to address, phone and email

      caregiverAddresses: [],
      caregiverPhones: [],
      caregiverEmails: [],

      caregiverAddresses2: [],
      caregiverPhones2: [],
      caregiverEmails2: [],

      homeAddress: {},
      mailingAddress: {},
      homePhone: {},
      cellPhone: {},

      homeAddress2: {},
      mailingAddress2: {},
      homePhone2: {},
      cellPhone2: {},

      //new input values:
      newMA: {},
      newHP: {},
      newCP: {},
      newEmail: {},
      newHA: {},
      errors: {},

      newMA2: {},
      newHP2: {},
      newCP2: {},
      newEmail2: {},
      newHA2: {}
    };
    this.editCancel = this.editCancel.bind(this);
    this.editCaregivers = this.editCaregivers.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.addContact = this.addContact.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleChangeP = this.handleChangeP.bind(this);
  }

  componentDidMount() {
    //primary contact
    const result = this.state.caregiver.filter(word => word.is_primary == true);
    var t = [];
    var r = [];
    var d = [];
    for (let i = 0; i < result.length; i++) {
      t.push(result[i].name);
      r.push(result[i].relationship);
      d.push(result[i].client_id);
    }

    this.setState({
      primaryName: t,
      primaryRelationship: r,
      newPN: t,
      newPR: r,
      id1: d
    });

    //second contact
    const result2 = this.state.caregiver.filter(
      word => word.is_primary == false
    );
    var t2 = [];
    var r2 = [];
    var d2 = [];
    for (let i = 0; i < result2.length; i++) {
      t2.push(result2[i].name);
      r2.push(result2[i].relationship);
      d2.push(result2[i].client_id);
    }

    this.setState({
      secondName: t2,
      secondRelationship: r2,
      newSPN: t2,
      newSPR: r2,
      id2: d2
    });
    this.setState({});

    //get caregivers' contact info
    ////////

    userService
      .getCaregiverContactInfo(
        JSON.parse(localStorage.getItem("oneUser")).client_id,
        JSON.parse(localStorage.getItem("oneUser")).token,
        1,
        this.state.id1
      )

      .then(data => {
        this.setState({
          caregiverAddresses: data[0][1],
          caregiverPhones: data[1][1],
          caregiverEmails: data[2][1]
        });
      });

    userService
      .getCaregiverContactInfo(
        JSON.parse(localStorage.getItem("oneUser")).client_id,
        JSON.parse(localStorage.getItem("oneUser")).token,
        1,
        this.state.id2
      )

      .then(data => {
        this.setState({
          caregiverAddresses2: data[0][1],
          caregiverPhones2: data[1][1],
          caregiverEmails2: data[2][1]
        });
      });

    ///////

    const ha = this.state.caregiverAddresses.filter(
      word => word.type == "home"
    );
    if (ha.length != 0) {
      this.setState({ homeAddress: ha[0].address, newHA: ha[0].address });
    } else {
      this.setState({ homeAddress: ha[0], newHA: ha[0] });
    }

    const ma = this.state.caregiverAddresses.filter(
      word => word.type == "mailing"
    );
    if (ma.length != 0) {
      this.setState({ mailingAddress: ma[0].address, newMA: ma[0].address });
    } else {
      this.setState({ mailingAddress: ma[0], newMA: ma[0] });
    }

    const cp = this.state.caregiverPhones.filter(word => word.type == "cell");
    if (cp.length != 0) {
      this.setState({
        cellPhone: "+" + cp[0].country_code + " " + cp[0].number,
        newCP: "+" + cp[0].country_code + " " + cp[0].number
      }); ///IF Differnet code????????
    } else {
      this.setState({ cellPhone: cp[0], newCP: cp[0] }); ///IF Differnet code????????
    }

    const hp = this.state.caregiverPhones.filter(word => word.type == "home");
    if (hp.length != 0) {
      this.setState({
        homePhone: "+" + hp[0].country_code + " " + hp[0].number,
        newHP: "+" + hp[0].country_code + " " + hp[0].number
      });
    } else {
      this.setState({ homePhone: hp[0], newHP: hp[0] });
    }

    this.setState({ newEmail: this.state.caregiverEmails });

    //get name and relationship without mapping in render and set state
  }
  componentWillMount() {}
  editCancel() {
    this.setState({
      isEditCaregivers: false
    });
  }
  editCaregivers() {
    this.setState({
      isEditCaregivers: true
    });
  }
  addContact() {
    this.setState({
      isAddContact: true
    });
    if (this.state.isAddContact) {
      return <AddContact />;
    }
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  handleChange2(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  //primary contact info change
  handleChangeP(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  TOKEN = JSON.parse(localStorage.getItem("oneUser")).token;
  ID = JSON.parse(localStorage.getItem("oneUser")).client_id;
  handleSubmit(event) {
    event.preventDefault();
    //primary
    if (
      this.state.newPN == this.state.primaryName &&
      this.state.newPR == this.state.primaryRelationship
    ) {
    } else {
      userService.editCaregivers(
        this.ID,
        this.TOKEN,
        this.state.newPN,
        this.state.newPR,
        true
      );
    }
    //second
    if (
      this.state.newSPN == this.state.secondName &&
      this.state.newSPR == this.state.secondRelationship
    ) {
    } else {
      userService.editCaregivers(
        this.ID,
        this.TOKEN,
        this.state.newSPN,
        this.state.newSPR,
        false
      );
    }
    //mailing address
    if (this.state.newMA == this.state.mailingAddress) {
    } else {
      userService.editCaregiverContacts(
        this.ID,
        this.TOKEN,
        "address",
        this.state.newMA,
        "mailing",
        1
      );
      this.setState({ mailingAddress: this.state.newMA });
    }

    //home address
    if (this.state.newHA == this.state.homeAddress) {
    } else {
      userService.editCaregiverContacts(
        this.ID,
        this.TOKEN,
        "address",
        this.state.newHA,
        "home",
        1
      );
      this.setState({ homeAddress: this.state.newHA });
    }

    //cell phone
    if (this.state.newCP == this.state.cellPhone) {
    } else {
      userService.editCaregiverContacts(
        this.ID,
        this.TOKEN,
        "phone",
        this.state.newCP,
        "cell",
        1
      );
      this.setState({ cellPhone: this.state.newCP });
    }
    //home phone
    if (this.state.newHP == this.state.homePhone) {
    } else {
      userService.editCaregiverContacts(
        this.ID,
        this.TOKEN,
        "phone",
        this.state.newHP,
        "home",
        1
      );
      this.setState({ homePhone: this.state.newHP });
    }

    //email
    if (this.state.newEmail == this.state.emailInfo) {
    } else {
      userService.editCaregiverContacts(
        this.ID,
        this.TOKEN,
        "email",
        this.state.newEmail,
        "main",
        1
      );
      this.setState({ emailInfo: this.state.newEmail });
    }

    this.setState({ isEditCaregivers: false });
  }
  render() {
    const {
      caregiver,
      isEditCaregivers,
      primaryName,
      primaryRelationship,
      secondName,
      secondRelationship,
      caregiverAddresses,
      caregiverPhones,
      caregiverEmails,
      homeAddress,
      newHA,
      id1
    } = this.state;

    console.log("not changable:" + secondName + secondRelationship);
    console.log("idsssss:" + JSON.stringify(id1));
    return (
      <div>
        {!isEditCaregivers ? (
          <div>
            {caregiver.map((careg, index) => (
              <div>
                {careg.is_primary ? (
                  <div>
                    <p>
                      <strong>Primary Contact:</strong>{" "}
                    </p>
                    <ul>
                      <li>
                        <strong>Name: </strong>
                        {careg.name}
                      </li>

                      <li>
                        <strong> Relationship:</strong> {careg.relationship}
                      </li>
                      <CaregiverContact id={careg.client_id} />
                    </ul>
                  </div>
                ) : (
                  <div>
                    <p>
                      <strong>Second Contact:</strong>{" "}
                    </p>
                    <ul>
                      <li>
                        {" "}
                        <strong>Name: </strong>
                        {careg.name}
                      </li>
                      <li>
                        <strong> Relationship:</strong> {careg.relationship}{" "}
                      </li>
                      <CaregiverContact id={careg.client_id} />
                    </ul>
                  </div>
                )}
              </div>
            ))}
            <Button variant="secondary" onClick={this.editCaregivers}>
              Edit
            </Button>
          </div>
        ) : (
          <div>
            {caregiver.map((careg, index) => (
              <div>
                {careg.is_primary ? (
                  <div>
                    <p>
                      <strong>Primary Contact:</strong>{" "}
                    </p>
                    <ul>
                      <li>
                        <strong>Name: </strong>{" "}
                        <input
                          value={this.state.newPN}
                          onChange={this.handleChange}
                          name="newPN"
                        />
                      </li>
                      <li>
                        <strong> Relationship:</strong>{" "}
                        <select
                          value={this.state.newPR}
                          onChange={this.handleChange}
                          name="newPR"
                        >
                          <option value="father">father</option>
                          <option value="mother">mother</option>
                        </select>
                      </li>
                      <li>
                        <strong> Home Address: </strong>{" "}
                        <input
                          placeholder="home address"
                          value={this.state.newHA || ""}
                          onChange={this.handleChangeP}
                          name="newHA"
                        />
                      </li>
                      <li>
                        <strong> Mailing Address:</strong>{" "}
                        <input
                          placeholder="mailing address"
                          value={this.state.newMA || ""}
                          onChange={this.handleChangeP}
                          name="newMA"
                        />
                      </li>
                      <li>
                        <strong>Cell Phone: </strong>
                        <input
                          placeholder="cell phone"
                          value={this.state.newCP || ""}
                          onChange={this.handleChangeP}
                          name="newCP"
                        />
                      </li>
                      <li>
                        <strong> Home Phone:</strong>{" "}
                        <input
                          placeholder="home phone"
                          value={this.state.newHP || ""}
                          onChange={this.handleChangeP}
                          name="newHP"
                        />
                      </li>
                      <strong> Email:</strong>
                      <input
                        type="email"
                        value={this.state.newEmail}
                        onChange={this.handleChangeP}
                        name="newEmail"
                      />
                    </ul>
                  </div>
                ) : (
                  <div>
                    <p>
                      <strong>Second Contact:</strong>{" "}
                    </p>
                    <ul>
                      <li>
                        <strong>Name: </strong>{" "}
                        <input
                          value={this.state.newSPN}
                          onChange={this.handleChange2}
                          name="newSPN"
                        />
                      </li>
                      <li>
                        <strong> Relationship:</strong>{" "}
                        <select
                          value={this.state.newSPR}
                          onChange={this.handleChange2}
                          name="newSPR"
                        >
                          <option value="father">father</option>
                          <option value="mother">mother</option>
                          <option value="uncle">uncle</option>
                          <option value="aunt">aunt</option>
                        </select>
                      </li>
                      <li>
                        <strong> Home Address: </strong>{" "}
                        <input
                          placeholder="home address"
                          value={this.state.newHA || ""}
                          onChange={this.handleChangeP}
                          name="newHA"
                        />
                      </li>
                      <li>
                        <strong> Mailing Address:</strong>{" "}
                        <input
                          placeholder="mailing address"
                          value={this.state.newMA || ""}
                          onChange={this.handleChangeP}
                          name="newMA"
                        />
                      </li>
                      <li>
                        <strong>Cell Phone: </strong>
                        <input
                          placeholder="cell phone"
                          value={this.state.newCP || ""}
                          onChange={this.handleChangeP}
                          name="newCP"
                        />
                      </li>
                      <li>
                        <strong> Home Phone:</strong>{" "}
                        <input
                          placeholder="home phone"
                          value={this.state.newHP || ""}
                          onChange={this.handleChangeP}
                          name="newHP"
                        />
                      </li>
                      <strong> Email:</strong>
                      <input
                        type="email"
                        value={this.state.newEmail}
                        onChange={this.handleChangeP}
                        name="newEmail"
                      />
                    </ul>
                  </div>
                )}
              </div>
            ))}

            <ButtonToolbar>
              <Button variant="secondary" onClick={this.handleSubmit}>
                Save
              </Button>
              <Button variant="secondary" onClick={this.editCancel}>
                Cancel
              </Button>
            </ButtonToolbar>
          </div>
        )}
      </div>
    );
  }
}

export default EditCaregivers;
